def underscore
    $journalistes
    compte = 0
    $journalistes.each do |journaliste|
        caractere = journaliste.split ""
        caractere.each do |i|
            if i.match("_")
                compte += 1

            end
        end
    end
    puts "il y a #{compte} fois le symbole underscore dans cette liste"
    return compte
end

